examples.Fibonacci:
chuongtrinh test
{
	bien i, n: nguyen;
	bien s: chuoi;
	ham fibonacci(x: nguyen)
	{
		bien ketqua: nguyen;
		neu(x <= 0)
		{
			ketqua = -1;
		};

		neu(x == 1) 
		{
			ketqua = 1;
		};

		neu(x == 2) 
		{
			ketqua = 1;
		};

		neu(x > 2) 
		{
			ketqua = fibonacci(x - 1) + fibonacci(x - 2);
		};

		trave ketqua;
	}
	i = 1;
	n = 10;
	s = n + " số đầu tiên trong dãy Fibonacci là:";
	in("Chào mừng đến với Newbie Interpreter:");
	in(s);
	khi(i <= n)
	{ 
		in(fibonacci(i));
		i = i + 1;
	};
}

examples.KiemTraSoChanLe:
chuongtrinh taolao
{
	bien s: chuoi;
	bien n: nguyen;
	bien kq: bool;
	ham laSoChan(x: nguyen)
	{
		bien ketqua: bool;
		neu(x % 2 == 0)
		{
			ketqua = dung;
		}
		nguoclai
		{
			ketqua = sai;
		};

		trave ketqua;
	}
	n = 1620;
	s = "Chương trình kiểm tra số chẵn hay lẻ của số " + n + ":";
	in("Chào mừng đến với Newbie Interpreter:");
	in(s);
	kq = laSoChan(n);
	neu(kq == dung)
	{
		in("Số " + n + " là số chẵn");
	}
	nguoclai
	{
		in("Số " + n + " là số lẻ");
	};
}

examples.KiemTraSoNguyenTo:
chuongtrinh test
{
	bien n: nguyen;
	bien s: chuoi;
	bien kq: bool;
	ham laSoNguyenTo(x: nguyen)
	{
		bien i: nguyen;
		bien ketqua: bool;
		ketqua = dung;
		i = 2;
		khi(i < x)
		{
			neu(x % i == 0)
			{
				ketqua = sai;
			};
			i = i + 1;
		};

		trave ketqua;
	}
	n = 19;
	s = "Chương trình kiểm tra số nguyên tố của số " + n + ": ";
	in("Chào mừng đến với Newbie Interpreter:");
	in(s);
	kq = laSoNguyenTo(n);
	neu(kq == dung)
	{
		in("Số " + n + " là số nguyên tố");
	}
	nguoclai
	{
		in("Số " + n + " không phải là số nguyên tố");
	};
}

examples.TinhCanBac2:
chuongtrinh taolao
{
	bien n: nguyen;
	bien s: chuoi;
	ham tinhCanBac2(x: thuc)
	{
		bien ketqua: thuc;
		bien xHienTai: thuc;
		bien saiSoHienTai: thuc;
		bien saiSoChoPhep: thuc;
		bien trai, phai: thuc;
		bien lonHonKetQua: bool;

		saiSoChoPhep = 0.000001;		
		trai = 0.0;
		phai = x;
		ketqua = (trai + phai) / 2;
		xHienTai = ketqua * ketqua;

		neu(xHienTai > x )
		{
			saiSoHienTai = xHienTai - x;
			lonHonKetQua = dung;
		}
		nguoclai
		{
			lonHonKetQua = sai;
			saiSoHienTai = x - xHienTai;
		};

		khi(saiSoHienTai > saiSoChoPhep)
		{
			neu(lonHonKetQua == dung)
			{
				phai = ketqua;				
			}
			nguoclai
			{
				trai = ketqua;
			};
			ketqua = (trai + phai) / 2;
			xHienTai = ketqua * ketqua;

			neu(xHienTai > x )
			{
				saiSoHienTai = xHienTai - x;
				lonHonKetQua = dung;
			}
			nguoclai
			{
				lonHonKetQua = sai;
				saiSoHienTai = x - xHienTai;
			};
		};

		trave ketqua;
	}
	n = 5;
	s = "Chương trình tính căn bậc 2 của  " + n + ":";
	in("Chào mừng đến với Newbie Interpreter:");
	in(s);
	in(tinhCanBac2(n));
}
